tinymce.PluginManager.add("badge",
    function(a){
        a.addCommand("InsertBadge",
            function(){
                a.execCommand("mceInsertContent",!1,"<span class='badge' >Badge</span>")}),
                a.addButton("badge",
                    {
                        icon:"badge",
                        tooltip:"badge",
                        cmd:"InsertBadge"
                    }),
                a.addMenuItem("badge",
                    {
                        icon:"badge",
                        text:"Badge Bootstrap",

                        cmd:"InsertBadge",
                        context:"tools"})

    });