tinymce.PluginManager.add("bbloq",
    function(a){
        a.addCommand("InsertBlockquote",
            function(){
                a.execCommand("mceInsertContent",!1,"" +
                "<blockquote><p>textp</p><footer>Rodape<cite>Source Title</cite></footer></blockquote>"
                )}),
            a.addButton("blockquote",
                {
                    icon:"blockquote",
                    tooltip:"blockquote",
                    cmd:"InsertBlockquote"
                }),
            a.addMenuItem("blockquote",
                {
                    icon:"blockquote",
                    text:"blockquote Bootstrap",

                    cmd:"InsertBlockquote",
                    context:"tools"})

    });
