tinymce.PluginManager.add("bimagem",
    function(a){
        a.addCommand("InsertImagem",
            function(){
                a.execCommand("mceInsertContent",!1,"" +
                "<img src='...' class='img-responsive' alt='...'>"
                )}),
            a.addButton("bimagem",
                {
                    icon:"bimagem",
                    tooltip:"bimagem",
                    cmd:"InsertBlockquote"
                }),
            a.addMenuItem("bimagem",
                {
                    icon:"bimagem",
                    text:"Imagem Bootstrap",

                    cmd:"InsertImagem",
                    context:"tools"})

    });
