tinymce.PluginManager.add("bprimary",
    function(a){
        a.addCommand("InsertButton",
            function(){
                a.execCommand("mceInsertContent",!1,"<a class='btn btn-primary'>Button</a>")}),
            a.addButton("button",
                {
                    icon:"button",
                    tooltip:"button",
                    cmd:"InsertButton"
                }),
            a.addMenuItem("button",
                {
                    icon:"button",
                    text:"Button Primary",

                    cmd:"InsertButton",
                    context:"tools"})

    });