tinymce.PluginManager.add("syntax",
    function(a){
        a.addCommand("InsertSyntax",
            function(){
                a.execCommand("mceInsertContent",!1,"<pre class='brush:js;'>brush: php, js, css, c#, java e xml</pre>")}),
            a.addButton("syntax",
                {
                    icon:"syntax",
                    tooltip:"syntax",
                    cmd:"InsertSyntax"
                }),
            a.addMenuItem("syntax",
                {
                    icon:"syntax",
                    text:"Sintaxe de Codigo",

                    cmd:"InsertSyntax",
                    context:"tools"})

    });